    public function getSourceContext(): Source
    {
        return new Source("{% extends 'baseF.html.twig' %}

{% block title %}Forgot Password - MatchupZ{% endblock %}

{% block body %}
    <div class=\"container-fluid py-5\">
        <div class=\"container\">
            <div class=\"row justify-content-center\">
                <div class=\"col-lg-4 col-md-6 col-sm-8\">
                    <div class=\"card p-4 shadow-sm\" style=\"max-width: 400px; margin: 0 auto;\">
                        <h2 class=\"text-center mb-4\">Forgot Password</h2>

                        {% for label, messages in app.flashes %}
                            {% for message in messages %}
                                <div class=\"alert alert-{{ label }} alert-dismissible fade show\" role=\"alert\">
                                    {{ message }}
                                    <button type=\"button\" class=\"btn-close\" data-bs-dismiss=\"alert\" aria-label=\"Close\"></button>
                                </div>
                            {% endfor %}
                        {% endfor %}

                        {{ form_start(form, {'attr': {'novalidate': 'novalidate'}}) }}
                            <div class=\"mb-3\">
                                {{ form_row(form.email, {'attr': {'class': 'form-control', 'placeholder': 'Entrez votre email'}}) }}
                            </div>
                            {{ form_row(form._token) }}

                            <div class=\"d-flex justify-content-between align-items-center mb-3\">
                                <button type=\"submit\" class=\"btn btn-primary\">Send Reset Code</button>
                                <a href=\"{{ path('app_user_index') }}\" class=\"text-muted small\">Back to Login</a>
                            </div>
                        {{ form_end(form) }}
                    </div>
                </div>
            </div>
        </div>
    </div>
{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link rel=\"stylesheet\" href=\"{{ asset('assets/vendor/fonts/boxicons.css') }}\" />
{% endblock %}", "user/forgot_password.html.twig", "C:\\Users\\Hazem Mrad\\Desktop\\integration\\New folder - Copy\\New folder\\matchupz-web-0\\templates\\user\\forgot_password.html.twig");
